import React, { useState } from "react";
import "./index.css";
import ReplyForm from "../ReplyForm";

const Comment = ({ comment, onDelete, onEdit, onReply }) => {
  const [isEditing, setEditing] = useState(false);
  const [replyText, setReplyText] = useState("");
  const [editedComment, setEditedComment] = useState(comment.text);
  const handleEdit = () => {
    setEditing(true);
  };

  const handleSaveEdit = () => {
    onEdit(comment.id, editedComment);
    setEditing(false);
  };
  const handleReply = () => {
    onReply(comment.id, replyText);
    setReplyText("");
  };
  return (
    // <div className="container">
    // </div>
    <div className="container">
      <div className="commentor">{comment.name}</div>
      {isEditing ? (
        <>
          <textarea
            style={{ padding: 20, marginLeft: 20 }}
            value={editedComment}
            onChange={(e) => setEditedComment(e.target.value)}
          />
          <button
            style={{ margin: "10px 0px 0px 20px" }}
            onClick={handleSaveEdit}
          >
            Save
          </button>
        </>
      ) : (
        <>
          <div style={{ padding: 20 }}>{comment.text}</div>
          <div style={{ display: "flex", padding: "0px 0px 0px 20px" }}>
            <button onClick={handleEdit}>Edit</button>
            <button onClick={() => onDelete(comment.id)}>Delete</button>
            <ReplyForm onReply={(reply) => onReply(comment.id, reply)} />
            {comment.replies && comment.replies.length > 0 && (
              <div style={{ marginLeft: "20px" }}>
                <strong>Replies:</strong>
                {comment.replies.map((reply) => (
                  <div key={reply.id}>
                    <div>{reply.name}</div>
                    <div>{reply.text}</div>
                    <button onClick={() => onDelete(reply.id)}>Delete</button>
                  </div>
                ))}
              </div>
            )}
          </div>
        </>
      )}
    </div>
  );
};
export default Comment;
